import { app } from "firebase-admin";
import TortoiseClient from "./TortoiseClient";
import App = app.App;

/**
 * Create a new TortoiseClient
 * @param {App} app - A firebase app
 * @return {TortoiseClient}
 */
export function createNewTortoiseClient(app: App): TortoiseClient {
  return new TortoiseClient(app);
}